INSERT INTO categories VALUES(1,'상호운용성',replace('•	프로토콜\n•	메타데이터\n	•	가상 공간 간 이동(teleportation) 규격\n	•	자산·아이템 이동성\n	•	공통 API 및 SDK 표준화','\n',char(10)));
INSERT INTO categories VALUES(2,'콘텐츠 및 상호작용',replace('•	3D 모델링 포맷 (glTF, USD 등)\n	•	VR/AR 상호작용 인터페이스\n	•	공간 오디오 및 몰입형 미디어 규격\n	•	햅틱 피드백 및 디바이스 상호운용성','\n',char(10)));
