  description TEXT
);
INSERT INTO categories VALUES(1,'상호운용성',replace('•	프로토콜\n•	메타데이터\n	•	가상 공간 간 이동(teleportation) 규격\n	•	자산·아이템 이동성\n	•	공통 API 및 SDK 표준화','\n',char(10)));
INSERT INTO categories VALUES(2,'콘텐츠 및 상호작용',replace('•	3D 모델링 포맷 (glTF, USD 등)\n	•	VR/AR 상호작용 인터페이스\n	•	공간 오디오 및 몰입형 미디어 규격\n	•	햅틱 피드백 및 디바이스 상호운용성','\n',char(10)));
INSERT INTO categories VALUES(3,'아바타','- 아바타 상호운용성 (의상, 액세서리, 표현 방식)');
INSERT INTO categories VALUES(4,'기타','메타버스와 관계될 수 있지만 다른 범주에 포함되지 않는 것들');
INSERT INTO categories VALUES(5,'신원',replace('- DID(Decentralized Identifier) 및 VC(Verifiable Credential)\n- 생체인식 기반 인증\n- 개인정보 보호 및 익명성 보장 규격','\n',char(10)));
INSERT INTO categories VALUES(6,'디지털 자산 및 경제',replace('	•	가상화폐 및 토큰 상호운용성\n	•	블록체인 기반 소유권 관리\n	•	NFT 발행·거래 규격\n	•	디지털 결제 및 정산 프로토콜','\n',char(10)));
INSERT INTO categories VALUES(7,'인프라 및 네트워크',replace('	•	초저지연 네트워크 (5G, 6G, Wi-Fi 7)\n	•	엣지 컴퓨팅 및 클라우드 인프라\n	•	데이터 전송 및 동기화 프로토콜\n	•	실시간 스트리밍 및 분산 처리 표준','\n',char(10)));
INSERT INTO categories VALUES(8,'보안 및 프라이버시',replace('	•	데이터 암호화 및 무결성 검증\n	•	안전한 자격 증명 공유\n	•	접근 제어 및 권한 관리\n	•	프라이버시 보존 연산 (예: 동형암호, MPC)','\n',char(10)));
INSERT INTO categories VALUES(9,'사회 및 문화',replace('	•	콘텐츠 등급 및 윤리적 가이드라인\n	•	아바타 표현의 다양성과 접근성\n	•	장애인 접근성 규격\n	•	글로벌 문화적 차이 반영 기준','\n',char(10)));
INSERT INTO categories VALUES(10,'AI 콘텐츠',replace('•	가상 공간·오브젝트 자동 생성 \n	•	가상 공간·오브젝트 자동 생성 AI 프레임워크, 파이프라인\n	•	AI 애니메이션, AI 리깅\n	•	AI 기반 아바타 자동 생성(외형·목소리·행동 패턴)\n	•	AI 기반 불법·유해 콘텐츠 탐지','\n',char(10)));
INSERT INTO categories VALUES(11,'AI 상호작용',replace('• NPC(Non-Player Character) 및 에이전트의 상호작용\n• 감정·표정·제스처 합성\n• 다국어 실시간 번역 및 자막 생성\n• 멀티모달 입력(제스처+음성+시선) 통합 API\n• 사용자 행동 데이터 기반 추천(이벤트, 아이템, 친구 매칭)\n','\n',char(10)));
