INSERT INTO tech_analysis_reports VALUES(54,'https://zdnet.co.kr/view/?no=20250820175232#_DYAD','인공지능(AI)과 메타버스 기술 융합이 새로운 산업 모델 창출과 사회 구조 변화에 영향 미칠 것이란 전망이 나왔다. 20일 소프트웨어정책연구소(SPRi) 보고서에 따르면 AI와 메타버스가 게임을 비롯한 교육, 제조 등 여러 산업에서 실증적 성과를 내고 있는 것으로 분석됐다…','2025-08-24 02:20:35','메타버스에 AI 붙였더니…”제조·국방·교육 산업 대전환”','https://image.zdnet.co.kr/2025/08/20/3ab2cbd7bb543d47795bc1661712ad0d.png',NULL,'산업메타버스','completed');
